set policy('Bandwidth Limit for Router-Laptop') {
 from endpoint('router') to endpoint('laptop') {
 set bandwidth('max', '10', 'mbps')}}

set policy('Smart Sensor Isolation') {
 for device('smart sensor') {
 allow traffic(device('smart sensor'), device('smart home hub')) 
 block traffic(device('smart sensor'), group('network'))}}
